BEGIN
	if (nm_tabela_p = 'CIRURGIA_AGENTE_ANEST_OCOR') then
		begin
		ds_comando_w	:= ' select a.'|| nm_campo_retorno_p ||' from '|| nm_tabela_origem_p ||' a,  CIRURGIA_AGENTE_ANESTESICO b, '|| nm_tabela_p ||' c  '
