CREATE OR REPLACE PROCEDURE obter_pontuacao_total_md ( qt_pto_cardio_p bigint, qt_pto_pulmonar_p bigint, qt_pto_hepatica_p bigint, qt_pto_sncr_p bigint, qt_pto_renal_p bigint, qt_pto_hematologica_p bigint, qt_pontuacao_p INOUT bigint, pr_mortalidade_p INOUT bigint ) AS $body$
DECLARE

    qt_pontuacao_w    smallint;
    logit_w           double precision := 0;
    pr_mortalidade_w  double precision;

BEGIN
    qt_pontuacao_w := ( coalesce(qt_pto_cardio_p, 0) + coalesce(qt_pto_pulmonar_p, 0) +
                        coalesce(qt_pto_hepatica_p, 0) + coalesce(qt_pto_sncr_p, 0) +
                        coalesce(qt_pto_renal_p, 0) + coalesce(qt_pto_hematologica_p, 0) );

    logit_w          := -7.64 + 0.30 * ( qt_pontuacao_w );
    pr_mortalidade_w := trunc(dividir_md(exp(logit_w), 1 + exp(logit_w)) * 100, 1);

    qt_pontuacao_p   := coalesce(qt_pontuacao_w,0);
    pr_mortalidade_p := coalesce(pr_mortalidade_w,0);

END;
$body$
