CREATE OR REPLACE PROCEDURE obter_portador_pf_pj (cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, cd_portador_p INOUT bigint, cd_tipo_portador_p INOUT bigint) AS $body$
DECLARE


cd_tipo_portador_estab_w	bigint;
cd_portador_estab_w		bigint;
cd_tipo_portador_w		bigint;
cd_portador_w			bigint;
cd_tipo_portador_pj_w		bigint;
cd_portador_pj_w		bigint;


BEGIN

select	coalesce(max(cd_tipo_portador),0),
	coalesce(max(cd_portador),0)
into STRICT	cd_tipo_portador_estab_w,
	cd_portador_estab_w
from	parametro_contas_receber
where	cd_estabelecimento	= cd_estabelecimento_p;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	cd_tipo_portador_w	:= cd_tipo_portador_estab_w;
	cd_portador_w		:= cd_portador_estab_w;
elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select	max(cd_tipo_portador),
		max(cd_portador)
	into STRICT	cd_tipo_portador_pj_w,
		cd_portador_pj_w
	from	pessoa_juridica_estab
	where	cd_cgc			= cd_cgc_p
	and	cd_estabelecimento	= cd_estabelecimento_p;

	if (coalesce(cd_tipo_portador_pj_w::text, '') = '') and (coalesce(cd_portador_pj_w::text, '') = '') then
		select	cd_tipo_portador,
			cd_portador
		into STRICT	cd_tipo_portador_pj_w,
			cd_portador_pj_w
		from	pessoa_juridica
		where	cd_cgc	= cd_cgc_p;
	end if;

	cd_tipo_portador_w	:= coalesce(cd_tipo_portador_pj_w, cd_tipo_portador_estab_w);
	cd_portador_w		:= coalesce(cd_portador_pj_w, cd_portador_estab_w);

end if;

cd_portador_p		:= cd_portador_w;
cd_tipo_portador_p	:= cd_tipo_portador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
