CREATE OR REPLACE PROCEDURE obter_preco_conv_mat_fornec (cd_convenio_p bigint, cd_cgc_p text, cd_material_p bigint, dt_referencia_p timestamp, vl_preco_p INOUT bigint) AS $body$
DECLARE


vl_preco_w	double precision;

c01 CURSOR FOR
SELECT	a.vl_material
from	convenio_preco_fornec b,
	convenio_preco_mat_fornec a
where	a.nr_seq_conv_preco	= b.nr_sequencia
and	b.cd_convenio		= cd_convenio_p
and	b.cd_cgc_fornec		= cd_cgc_p
and	a.cd_material		= cd_material_p
and	dt_referencia_p	between b.dt_inicio_vigencia and fim_dia(b.dt_fim_vigencia)
order by
	b.dt_inicio_vigencia;


BEGIN

open c01;
loop
fetch c01 into
	vl_preco_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	vl_preco_w	:= vl_preco_w;
end loop;
close c01;

vl_preco_p	:=	vl_preco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
