CREATE OR REPLACE PROCEDURE obter_preco_itens_nf ( cd_estabelecimento_p bigint, nr_sequencia_p bigint, cd_tabela_p bigint) AS $body$
DECLARE


nr_item_nf_w		integer;
cd_material_w		integer;
qt_material_w		double precision;
vl_preco_w		double precision;
vl_desconto_w		double precision;
vl_total_item_nf_w		double precision;


c01 CURSOR FOR
SELECT	nr_item_nf,
	cd_material,
	qt_item_nf,
	vl_desconto,
	coalesce(obter_preco_mat_tab(cd_estabelecimento_p, cd_material, cd_tabela_p),0)
from 	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p
and	(cd_material IS NOT NULL AND cd_material::text <> '');


BEGIN

open c01;
loop
fetch c01 into
	nr_item_nf_w,
	cd_material_w,
	qt_material_w,
	vl_desconto_w,
	vl_preco_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	vl_total_item_nf_w	:=	(vl_preco_w * qt_material_w);

	update	nota_fiscal_item
	set	vl_unitario_item_nf	= vl_total_item_nf_w / qt_material_w,
		vl_liquido		= vl_total_item_nf_w - vl_desconto_w,
		vl_total_item_nf	= vl_total_item_nf_w
	where	nr_item_nf	= nr_item_nf_w
	and	nr_sequencia	= nr_sequencia_p;
	commit;
	end;
end loop;
close c01;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
