CREATE OR REPLACE PROCEDURE obter_preco_material_entrada ( cd_material_p bigint, cd_estabelecimento_p bigint, cd_operacao_nf_p bigint, nm_usuario_p text, vl_unitario_item_nf_p INOUT bigint) AS $body$
DECLARE


nr_seq_nota_w		nota_fiscal.nr_sequencia%type;
vl_uni_item_nf_w	nota_fiscal_item.vl_unitario_item_nf%type;


BEGIN

select	coalesce(max(a.nr_sequencia),0)
into STRICT		nr_seq_nota_w
from		nota_fiscal a,
		nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and 		a.cd_operacao_nf = cd_operacao_nf_p
and		(a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
and		b.cd_material	= cd_material_p
and		a.cd_estabelecimento = cd_estabelecimento_p
and 		a.ie_situacao = 1
and 		obter_se_nota_entrada_saida(a.nr_sequencia) = 'E';

if (coalesce(nr_seq_nota_w, 0) > 0) then
	
	select	coalesce(max(vl_unitario_item_nf),0)
	into STRICT		vl_uni_item_nf_w
	from		nota_fiscal_item
	where	nr_sequencia = nr_seq_nota_w
	and		cd_material = cd_material_p;
			
end if;

vl_unitario_item_nf_p := coalesce(vl_uni_item_nf_w, 0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
