CREATE OR REPLACE PROCEDURE obter_proced_espec_medica (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_responsavel_credito_p text, ie_emite_conta_p text, ie_emite_conta_honor_p text, ie_clinica_p bigint, cd_Setor_atendimento_p bigint, cd_especialidade_p INOUT bigint, ie_funcao_medico_p INOUT text, cd_medico_exec_p text, nr_seq_proc_interno_p bigint, ie_tipo_atendimento_p bigint) AS $body$
DECLARE


cd_grupo_w			bigint;
cd_especialidade_w		bigint;
cd_especial_proc_w		bigint;
cd_area_w			bigint;
CD_FUNCAO_MEDICO_w		bigint;
ie_responsavel_credito_w	varchar(5);
ie_emite_conta_w		varchar(3);
ie_emite_conta_honor_w		varchar(3);
ie_clinica_w			integer;
cd_setor_Atendimento_w		integer;
nr_seq_forma_org_w              proced_espec_medica.nr_seq_forma_org%type;
nr_seq_subgrupo_w               proced_espec_medica.nr_seq_subgrupo%type;
nr_seq_grupo_w                  proced_espec_medica.nr_seq_grupo%type;

c01 CURSOR FOR
SELECT	cd_funcao_medico,
	cd_especialidade
from	proced_espec_medica
where	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p, 0)) = coalesce(cd_estabelecimento_p, 0)
and	coalesce(cd_convenio, cd_convenio_p)			= cd_convenio_p
and	coalesce(cd_grupo_proced,cd_grupo_w)			= cd_grupo_w
and	coalesce(cd_especial_proced, cd_especial_proc_w)	= cd_especial_proc_w
and	coalesce(cd_area_proced, cd_area_w)			= cd_area_w
and	coalesce(cd_procedimento, cd_procedimento_p)		= cd_procedimento_p
and	((coalesce(ie_origem_proced, ie_origem_proced_p)	= ie_origem_proced_p) or (coalesce(cd_procedimento::text, '') = ''))
and	coalesce(ie_responsavel_credito,coalesce(ie_responsavel_credito_w,'0'))	= coalesce(ie_responsavel_credito_w,'0')
and	coalesce(ie_emite_conta,coalesce(ie_emite_conta_w,'0'))			= coalesce(ie_emite_conta_w,'0')
and	coalesce(ie_emite_conta_honor,coalesce(ie_emite_conta_honor_w,'0'))	= coalesce(ie_emite_conta_honor_w,'0')
and	coalesce(cd_setor_Atendimento,coalesce(cd_Setor_atendimento_w,0))	= coalesce(cd_Setor_atendimento_w,0)
and	coalesce(cd_medico,coalesce(cd_medico_exec_p,'0'))	= coalesce(cd_medico_exec_p,'0')
and	coalesce(ie_clinica,coalesce(ie_clinica_w,0))		= coalesce(ie_clinica_w,0)
and	coalesce(nr_seq_proc_interno,coalesce(nr_seq_proc_interno_p,0))	= coalesce(nr_seq_proc_interno_p,0)
and	coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0)) = coalesce(ie_tipo_atendimento_p,0)
and	coalesce(nr_seq_forma_org,coalesce(nr_seq_forma_org_w,0)) = coalesce(nr_seq_forma_org_w,0)
and	coalesce(nr_seq_subgrupo,coalesce(nr_seq_subgrupo_w,0)) = coalesce(nr_seq_subgrupo_w,0)
and	coalesce(nr_seq_grupo,coalesce(nr_seq_grupo_w,0)) = coalesce(nr_seq_grupo_w,0)
order 	by coalesce(cd_convenio,0),
	coalesce(nr_seq_proc_interno,0),
	coalesce(cd_procedimento, 0),
	coalesce(cd_grupo_proced, 0),
	coalesce(cd_especial_proced, 0),
	coalesce(cd_area_proced, 0),
	coalesce(ie_responsavel_credito,'A'),
	coalesce(ie_emite_conta,'A'),
	coalesce(ie_emite_conta_honor,'A'),
	coalesce(cd_setor_Atendimento,0),
	coalesce(ie_clinica,0),
	coalesce(cd_medico,0),
	coalesce(ie_tipo_atendimento,0),
	coalesce(nr_seq_forma_org,0),
	coalesce(nr_seq_subgrupo,0),
	coalesce(nr_seq_grupo,0);

c01_w	c01%rowtype;


BEGIN

cd_funcao_medico_w		:= null;
cd_especialidade_w		:= null;
ie_responsavel_credito_w	:= coalesce(ie_responsavel_credito_p,'0');
ie_emite_conta_w		:= coalesce(ie_emite_conta_p,'0');
ie_emite_conta_honor_w		:= coalesce(ie_emite_conta_honor_p,'0');
ie_clinica_w			:= coalesce(ie_clinica_p,0);
cd_Setor_atendimento_w		:= coalesce(cd_Setor_atendimento_p,0);
nr_seq_forma_org_w              := null;
nr_seq_subgrupo_w               := null;
nr_seq_grupo_w                  := null;

begin
select b.cd_grupo_proc,
	b.cd_especialidade,
	b.cd_area_procedimento
into STRICT	cd_grupo_w,
	cd_especial_proc_w,
	cd_area_w
from	estrutura_procedimento_v b
where 	b.cd_procedimento	= cd_procedimento_p
and	b.ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	cd_grupo_w		:= 0;
	cd_especial_proc_w	:= 0;
	cd_area_w		:= 0;
	end;

begin
select  b.nr_seq_forma_org,
	b.nr_seq_subgrupo,
	b.nr_seq_grupo
into STRICT	nr_seq_forma_org_w,
	nr_seq_subgrupo_w,
	nr_seq_grupo_w
from	sus_estrutura_procedimento_v b
where 	b.cd_procedimento	= cd_procedimento_p
and	b.ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	nr_seq_forma_org_w	:= 0;
	nr_seq_subgrupo_w	:= 0;
	nr_seq_grupo_w		:= 0;
	end;

begin
for c01_w in c01 loop
	begin
	cd_funcao_medico_w	:= c01_w.cd_funcao_medico;
	cd_especialidade_w	:= c01_w.cd_especialidade;
	end;
end loop;
exception
	when others then
	cd_funcao_medico_w		:= null;
	cd_especialidade_w		:= null;
	end;
	
cd_especialidade_p		:= cd_especialidade_w;
ie_funcao_medico_p		:= cd_funcao_medico_w;

end;
$body$
