CREATE OR REPLACE PROCEDURE obter_processo_adep_js ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint, nr_etapa_hor_p bigint, ie_forma_gestao_p text, nm_usuario_p text, ie_acm_sn_p INOUT text, nr_processo_p INOUT bigint) AS $body$
DECLARE


dt_horario_w	timestamp;
nr_sequencia_w	bigint;
ie_acm_sn_w	varchar(1);


BEGIN

if (ie_forma_gestao_p = 'H') then

	select 	max(dt_horario)
	into STRICT	dt_horario_w
	from 	prescr_mat_hor 
	where 	nr_prescricao = nr_prescricao_p 
	and 	nr_seq_solucao = nr_seq_solucao_p 
	and 	nr_etapa_sol = nr_etapa_hor_p
	and		coalesce(ie_horario_especial, 'N') = 'N'
	and		coalesce(dt_suspensao::text, '') = '';

	select 	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from 	adep_processo 
	where 	nr_atendimento = nr_atendimento_p 
	and 	dt_horario_processo = dt_horario_w 
	and 	nr_prescricao = nr_prescricao_p 
	and 	nr_seq_solucao = nr_seq_solucao_p 
	and 	nr_etapa = nr_etapa_hor_p
	and		coalesce(dt_cancelamento::text, '') = '';
	
else
	select 	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from 	adep_processo
	where 	nr_atendimento = nr_atendimento_p 
	and 	nr_prescricao = nr_prescricao_p 
	and 	nr_seq_solucao = nr_seq_solucao_p 
	and 	nr_etapa = nr_etapa_p;
end if;

ie_acm_sn_w	:= obter_se_sol_acm_sn(nr_prescricao_p, nr_seq_solucao_p);

ie_acm_sn_p	:= ie_acm_sn_w;
nr_processo_p	:= nr_sequencia_w;

end;
