CREATE OR REPLACE PROCEDURE obter_produto_financeiro (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_cgc_p text, nr_seq_produto_p INOUT bigint, ie_tipo_convenio_p bigint, cd_sistema_externo_p text, nr_seq_grupo_p INOUT bigint) AS $body$
DECLARE


nr_seq_produto_w	bigint;
cd_tipo_pessoa_w	bigint;
nr_seq_grupo_w		bigint;
nr_seq_sub_grupo_w	bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia,
	b.nr_seq_grupo
from	produto_financeiro b,
	produto_financ_regra a
where	a.nr_seq_produto					= b.nr_sequencia
and	coalesce(b.cd_estabelecimento,cd_estabelecimento_p)		= cd_estabelecimento_p
and	coalesce(a.cd_convenio, coalesce(cd_convenio_p,0))		= coalesce(cd_convenio_p,0)
and	coalesce(a.cd_cgc, coalesce(cd_cgc_p,0))			= coalesce(cd_cgc_p,0)
and	coalesce(a.ie_tipo_convenio, coalesce(ie_tipo_convenio_p, 0))	= coalesce(ie_tipo_convenio_p, 0)
and	coalesce(a.cd_tipo_pessoa, cd_tipo_pessoa_w)		= cd_tipo_pessoa_w
and	coalesce(a.cd_externo,coalesce(cd_sistema_externo_p,'X'))		= coalesce(cd_sistema_externo_p,'X')
order 	by coalesce(a.cd_convenio, 0),
   	coalesce(a.cd_cgc, '0'),
	coalesce(ie_tipo_convenio_p, 0),
	coalesce(cd_tipo_pessoa,0),
	coalesce(a.cd_externo,'') desc;


BEGIN

select	coalesce(max(cd_tipo_pessoa), 0)
into STRICT	cd_tipo_pessoa_w
from	pessoa_juridica
where	cd_cgc	= cd_cgc_p;

nr_seq_produto_w	:= null;
nr_seq_grupo_w		:= null;
open c01;
loop
fetch c01 into
	nr_seq_produto_w,
	nr_seq_sub_grupo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

select	max(nr_seq_grupo_sup)
into STRICT	nr_seq_grupo_w
from	grupo_prod_financ
where	nr_sequencia	= nr_seq_sub_grupo_w;

nr_seq_produto_p	:= nr_seq_produto_w;
nr_seq_grupo_p	:= nr_seq_grupo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
