SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_qt_diag_def ( nr_atendimento_p bigint) AS $body$
DECLARE


qt_diagnostico_w	bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select count(*)
	into STRICT	qt_diagnostico_w
	from	DIAGNOSTICO_DOENCA
	where	nr_atendimento 		= nr_atendimento_p
	and		ie_tipo_diagnostico	IN (2, 3);
	
end if;

if (qt_diagnostico_w = 0) then
	update 	DIAGNOSTICO_DOENCA
	set 	ie_tipo_diagnostico	= 2
	where 	nr_atendimento = nr_atendimento_p
	and		dt_diagnostico = (SELECT max(x.dt_diagnostico)
					      from diagnostico_doenca x
					      where  x.nr_atendimento = nr_atendimento_p);
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
