CREATE OR REPLACE PROCEDURE obter_qt_dias_post_sc ( cd_estabelecimento_p bigint, cd_material_p bigint, nm_usuario_p text, qt_dias_postergar_p INOUT bigint, ie_somente_dia_util_p INOUT text) AS $body$
DECLARE


cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
qt_dias_postergar_w			integer	:= 0;
ie_somente_dia_util_w			varchar(1)	:= 'S';

c01 CURSOR FOR
SELECT	qt_dias_postergar,
	coalesce(ie_somente_dia_util,'S')
from	regra_postergar_entrega_sc
where	cd_estabelecimento = cd_estabelecimento_p
and	ie_situacao = 'A'
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

select	a.cd_grupo_material,
	a.cd_subgrupo_material,
	a.cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	qt_dias_postergar_w,
	ie_somente_dia_util_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_dias_postergar_w 	:= qt_dias_postergar_w;
	ie_somente_dia_util_w	:= ie_somente_dia_util_w;
	end;
end loop;
close C01;


qt_dias_postergar_p 	:= coalesce(qt_dias_postergar_w,0);
ie_somente_dia_util_p	:= coalesce(ie_somente_dia_util_w,'N');

commit;

end;
$body$
