CREATE OR REPLACE PROCEDURE obter_recibo_controle_bancario (cd_estabelecimento_p bigint, nr_seq_trans_fin_p bigint, nr_recibo_atual_p INOUT bigint) AS $body$
DECLARE


nr_recibo_atual_w	bigint;
ie_recibo_cb_w		varchar(255);


BEGIN

select	coalesce(max(ie_recibo_cb),'N')
into STRICT	ie_recibo_cb_w
from	transacao_financeira
where	nr_sequencia	= nr_seq_trans_fin_p;

nr_recibo_atual_w	:= 0;

if (ie_recibo_cb_w = 'S') then

	select	coalesce(max(nr_recibo_atual),0)
	into STRICT	nr_recibo_atual_w
	from	parametro_controle_banc
	where	cd_estabelecimento	= cd_estabelecimento_p;

	if (nr_recibo_atual_w <> 0) then

		update	parametro_controle_banc
		set	nr_recibo_atual		= nr_recibo_atual_w + 1
		where	cd_estabelecimento	= cd_estabelecimento_p;

		commit;
	end if;

end if;

nr_recibo_atual_p	:= nr_recibo_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
