CREATE OR REPLACE PROCEDURE obter_registros_laudo_pac_js ( nr_atendimento_p bigint, nr_atend_sus_p bigint, ie_mudanca_p text, dt_emissao_p timestamp, nr_aih_p INOUT bigint, nr_sequencia_p INOUT bigint, cd_cid_principal_p INOUT text, cd_cid_secundario_p INOUT text, cd_proc_original_p INOUT text, ds_proc_original_p INOUT text) AS $body$
DECLARE

 
nr_aih_w		bigint;
nr_sequencia_w		bigint;
cd_cid_principal_w	varchar(10);
cd_cid_secundario_w	varchar(10);
cd_proc_original_w	varchar(240);
ds_proc_original_w	PROCEDIMENTO.DS_PROCEDIMENTO%type;


BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0)then 
	begin 
	 
	select max(nr_aih), 
		max(nr_sequencia) 
	into STRICT	nr_aih_w, 
		nr_sequencia_w 
	from 	sus_aih_unif  
	where 	nr_atendimento = nr_atendimento_p;
	 
	select 	max(cd_doenca) 
	into STRICT	cd_cid_principal_w	 
	from 	diagnostico_doenca 
	where 	nr_atendimento = nr_atend_sus_p 
	and 	ie_classificacao_doenca = 'P';
	 
	select 	max(cd_doenca) 
	into STRICT	cd_cid_secundario_w 
	from 	diagnostico_doenca 
	where 	nr_atendimento = nr_atend_sus_p 
	and 	ie_classificacao_doenca = 'S';
 
	if (ie_mudanca_p = 'S') then 
		 
		cd_proc_original_w := substr(sus_obter_proc_orig_laudo(nr_aih_w,nr_aih_w,nr_atendimento_p,dt_emissao_p,1),1,240);
	 
		ds_proc_original_w := substr(sus_obter_proc_orig_laudo(nr_aih_w,nr_aih_w,nr_atendimento_p,dt_emissao_p,2),1,255);
	end if;
	end;
end if;
 
nr_aih_p		:= nr_aih_w;
nr_sequencia_p		:= nr_sequencia_w;
cd_cid_principal_p	:= cd_cid_principal_w;
cd_cid_secundario_p	:= cd_cid_secundario_w;
cd_proc_original_p	:= cd_proc_original_w;
ds_proc_original_p	:= ds_proc_original_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
