CREATE OR REPLACE PROCEDURE obter_registro_ligado_deposito ( nr_seq_deposito_p bigint, nr_seq_conta_banco_p bigint, cd_moeda_p bigint) AS $body$
DECLARE


qt_titulo_w	double precision;
qt_cheque_w	double precision;
qt_baixa_w	double precision;
qt_credito_w	double precision;


BEGIN

select	coalesce(max(t.cd_moeda),cd_moeda_p)
into STRICT	qt_titulo_w
from	deposito_ident_titulo t
where	t.nr_seq_deposito = nr_seq_deposito_p;

select	coalesce(max(a.cd_moeda),cd_moeda_p)
into STRICT	qt_cheque_w
from	deposito_ident_cheque a
where	a.nr_seq_deposito = nr_seq_deposito_p;

select	coalesce(max(b.cd_moeda),cd_moeda_p)
into STRICT	qt_baixa_w
from	deposito_ident_baixa b
where	b.nr_seq_deposito_ident = nr_seq_deposito_p;

select	coalesce(max(a.cd_moeda),cd_moeda_p)
into STRICT	qt_credito_w
from	movto_banco_pend a
where	a.nr_seq_conta_banco = nr_seq_conta_banco_p
and	exists (	SELECT	1
		from	movto_banco_pend_baixa b
		where	b.nr_seq_deposito_ident = nr_seq_deposito_p
		and	b.nr_seq_movto_pend = a.nr_sequencia);

if (qt_titulo_w <> cd_moeda_p) or (qt_cheque_w <> cd_moeda_p) or (qt_baixa_w <> cd_moeda_p) or (qt_credito_w <> cd_moeda_p) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(347875);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
