CREATE OR REPLACE PROCEDURE obter_regras_homecare (ie_classificacao_p text, nr_atendimento_p bigint default null, ie_exige_justi_p INOUT text DEFAULT NULL) AS $body$
DECLARE
								
										 
ie_justificar_w				varchar(1);
ie_classificacao_w varchar(20);
ie_exige_justi_w			varchar(1);

C01 CURSOR FOR
	SELECT 	b.ie_justificar
	from	regra_regulacao a,
			regra_regulacao_itens b
	where	a.nr_sequencia = b.nr_seq_regra_reg
	and		a.ie_tipo = 'SH'
	and		coalesce(a.ie_classificacao, coalesce(ie_classificacao_p,0)) = coalesce(ie_classificacao_p,0)
	and		obter_se_regulacao_regra_lib(a.ie_somente_ref,nr_atendimento_p,b.nr_sequencia) = 'S'
	and		coalesce(b.ie_situacao,'A') = 'A'
	order by   coalesce(a.ie_classificacao, 0);
									

BEGIN			


open C01;
loop
fetch C01 into	
	ie_justificar_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	  ie_exige_justi_w := ie_justificar_w;
	end;
end loop;
close C01;

ie_exige_justi_p 	:= ie_exige_justi_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
