CREATE OR REPLACE PROCEDURE obter_regras_imp_guia (cd_convenio_p bigint, cd_estabelecimento_p bigint, cd_prestador_p text, ie_importar_registro_p INOUT text) AS $body$
DECLARE


ie_importar_w	varchar(255) :='';
cont_w		bigint;

c01 CURSOR FOR
SELECT	coalesce(ie_importar,'N')
from	convenio_regra_imp_guia
where	cd_convenio		= cd_convenio_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	((coalesce(cd_prestador::text, '') = '') or (cd_prestador = cd_prestador_p));


BEGIN

ie_importar_w	:= 'N';

select	count(*)
into STRICT	cont_w
from	convenio_regra_imp_guia
where	cd_convenio		= cd_convenio_p
and	cd_estabelecimento		= cd_estabelecimento_p;

if (cont_w = 0) then
	ie_importar_w	:= 'S';
elsif (cont_w > 0) then

	ie_importar_w	:= 'N';

	open c01;
	loop
	fetch c01 into
		ie_importar_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ie_importar_w	:= ie_importar_w;
	end loop;
	close c01;

end if;

ie_importar_registro_p	:= ie_importar_w;

end;
$body$
