CREATE OR REPLACE PROCEDURE obter_regras_regulacao_cuidado ( nr_seq_motivo_p bigint, nr_atendimento_p bigint default null, ie_exige_justi_p INOUT text DEFAULT NULL, nr_seq_aval_p INOUT bigint DEFAULT NULL, ds_mensagem_p INOUT text DEFAULT NULL, nr_seq_item_pos_p INOUT bigint DEFAULT NULL) AS $body$
DECLARE


ie_justificar_w				varchar(1);
nr_seq_avaliacao_w			bigint;

ie_exige_justi_w			varchar(1);
nr_seq_aval_w				bigint;
ds_mensagem_w				varchar(255);
nr_seq_item_pos_w			bigint;

C01 CURSOR FOR
	SELECT 	b.ie_justificar,
			b.nr_seq_avaliacao
	from	regra_regulacao a,
			regra_regulacao_itens b
	where	a.nr_sequencia = b.nr_seq_regra_reg
	and		a.ie_tipo = 'TC'
	and		coalesce(a.nr_seq_motivo_solic_externa, coalesce(nr_seq_motivo_p,0)) = coalesce(nr_seq_motivo_p,0)
	and		obter_se_regulacao_regra_lib(a.ie_somente_ref,nr_atendimento_p,b.nr_sequencia) = 'S'
	and		coalesce(b.ie_situacao,'A') = 'A'
	order by   coalesce(a.nr_seq_motivo_solic_externa, 0);


BEGIN


open C01;
loop
fetch C01 into
	ie_justificar_w,
	nr_seq_avaliacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	  ie_exige_justi_w := ie_justificar_w;
	  nr_seq_aval_w := nr_seq_avaliacao_w;
	end;
end loop;
close C01;

nr_seq_aval_p 	 	:= nr_seq_aval_w;
ie_exige_justi_p 	:= ie_exige_justi_w;
ds_mensagem_p		:= ds_mensagem_w;
nr_seq_item_pos_p	:= nr_seq_item_pos_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
