SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_regras_regulacao_medic ( cd_material_p bigint, nr_atendimento_p bigint default null, ie_exige_justi_p INOUT text DEFAULT NULL, nr_seq_aval_p INOUT bigint DEFAULT NULL, ds_mensagem_p INOUT text DEFAULT NULL, nr_seq_item_pos_p INOUT bigint DEFAULT NULL) AS $body$
DECLARE
								
										 
ie_justificar_w				varchar(1);
nr_seq_avaliacao_w			bigint;

ie_exige_justi_w			varchar(1);
nr_seq_aval_w				bigint;
ds_mensagem_w				varchar(255);
nr_seq_item_pos_w			bigint;

ds_sql_consulta_w			varchar(4000);
ds_sql_w					varchar(4000);
customSQL_w					varchar(4000);
ie_resultado_w				varchar(80);
ds_titulo_w					varchar(80);
ds_documentacao_w			varchar(200);
nm_usuario_w				varchar(255);

C01 CURSOR FOR
	SELECT 	b.ie_justificar,
			b.nr_seq_avaliacao,
			b.ds_sql,
			b.ds_titulo,
			b.ds_documentacao
	from	regra_regulacao a,
			regra_regulacao_itens b
	where	a.nr_sequencia = b.nr_seq_regra_reg
	and		a.ie_tipo = 'ME'
	and		coalesce(a.cd_material, coalesce(cd_material_p,0)) = coalesce(cd_material_p,0)
	and		coalesce(b.ie_situacao,'A') = 'A'
	and		obter_se_regulacao_regra_lib(a.ie_somente_ref,nr_atendimento_p,b.nr_sequencia) = 'S'	
	order by   coalesce(a.cd_material, 0);
									

BEGIN

select	wheb_usuario_pck.get_nm_usuario
into STRICT	nm_usuario_w
;


Delete from consist_regulacao_itens
where   nm_usuario = nm_usuario_w
and		nr_atendimento = nr_atendimento_p;

commit;

ds_mensagem_p := 'N';
open C01;
loop
fetch C01 into	
	ie_justificar_w,
	nr_seq_avaliacao_w,
	ds_sql_w,
	ds_titulo_w,
	ds_documentacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	  ie_exige_justi_w := ie_justificar_w;
	  nr_seq_aval_w := nr_seq_avaliacao_w;
	  ds_sql_consulta_w := ds_sql_w;
	end;
end loop;
close C01;

customSQL_w :=  ' select nvl(max(''S''), ''N'') ' ||
				' from  atendimento_paciente a'     ||
				' where  a.nr_atendimento = :nr_atendimento  '   ||
				ds_sql_consulta_w;

EXECUTE customSQL_w
into STRICT   ie_resultado_w
using  nr_atendimento_p;

if (ie_resultado_w = 'N') then

	ds_mensagem_p := 'S';
					
	insert into consist_regulacao_itens(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario_nrec,
		nm_usuario,
		nr_atendimento,
		nr_seq_origem,	
		ds_inconsistencia,	
		ds_documentacao)		
	values (
		nextval('consist_regulacao_itens_seq'),
		clock_timestamp(),
		nm_usuario_w,
		nm_usuario_w,
		nr_atendimento_p,
		cd_material_p,
		ds_titulo_w,
		ds_documentacao_w);
		
		commit;
end if;


nr_seq_aval_p 	 	:= nr_seq_aval_w;
ie_exige_justi_p 	:= ie_exige_justi_w;
nr_seq_item_pos_p	:= nr_seq_item_pos_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_regras_regulacao_medic ( cd_material_p bigint, nr_atendimento_p bigint default null, ie_exige_justi_p INOUT text DEFAULT NULL, nr_seq_aval_p INOUT bigint DEFAULT NULL, ds_mensagem_p INOUT text DEFAULT NULL, nr_seq_item_pos_p INOUT bigint DEFAULT NULL) FROM PUBLIC;

