CREATE OR REPLACE PROCEDURE obter_regras_regulacao ( cd_especialidade_p bigint, cd_medico_dest_p text, nr_atendimento_p bigint default null, ie_exige_justi_p INOUT text DEFAULT NULL, nr_seq_aval_p INOUT bigint DEFAULT NULL) AS $body$
BEGIN

	select	max(ie_justificar),
			max(nr_seq_avaliacao)
	into STRICT	ie_exige_justi_p,
			nr_seq_aval_p
	from (
		SELECT	a.dt_atualizacao,
				b.ie_justificar,
				b.nr_seq_avaliacao
		from	regra_regulacao a,
				regra_regulacao_itens b
		where	a.nr_sequencia = b.nr_seq_regra_reg
		and		a.cd_medico_dest = cd_medico_dest_p
		and		a.ie_tipo = 'EN'
		and		(a.nr_seq_grupo_regulacao IS NOT NULL AND a.nr_seq_grupo_regulacao::text <> '')
		and		coalesce(b.ie_situacao,'A') = 'A'
		and		obter_se_regulacao_regra_lib(a.ie_somente_ref,nr_atendimento_p,b.nr_sequencia) = 'S'
		
union

		SELECT	a.dt_atualizacao,
				b.ie_justificar,
				b.nr_seq_avaliacao
		from	regra_regulacao a,
				regra_regulacao_itens b
		where	a.nr_sequencia = b.nr_seq_regra_reg
		and		a.cd_especialidade = cd_especialidade_p
		and		a.ie_tipo = 'EN'
		and		(a.nr_seq_grupo_regulacao IS NOT NULL AND a.nr_seq_grupo_regulacao::text <> '')
		and		obter_se_regulacao_regra_lib(a.ie_somente_ref,nr_atendimento_p,b.nr_sequencia) = 'S'
		and		coalesce(b.ie_situacao,'A') = 'A'
		and		not exists (	select	1
								from	regra_regulacao x
								where	x.cd_medico_dest = cd_medico_dest_p
								and		a.ie_tipo = 'EN'
								and		(a.nr_seq_grupo_regulacao IS NOT NULL AND a.nr_seq_grupo_regulacao::text <> ''))
		order	by DT_ATUALIZACAO desc
	) alias10 LIMIT 1;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_regras_regulacao ( cd_especialidade_p bigint, cd_medico_dest_p text, nr_atendimento_p bigint default null, ie_exige_justi_p INOUT text DEFAULT NULL, nr_seq_aval_p INOUT bigint DEFAULT NULL) FROM PUBLIC;

