CREATE OR REPLACE PROCEDURE obter_regra_acao_pag_duplic ( dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_regra_p INOUT bigint, ie_acao_p INOUT text) AS $body$
DECLARE


nr_seq_regra_w			bigint	:= null;
ie_acao_w			varchar(3)	:= 'N';

C01 CURSOR FOR
	SELECT	nr_sequencia,
		coalesce(ie_acao,'NC')
	from	regra_acao_pag_duplic
	where	coalesce(dt_referencia_p,clock_timestamp()) between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,coalesce(dt_referencia_p,clock_timestamp())))
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	cd_estabelecimento = cd_estabelecimento_p
	order by	dt_liberacao asc,
			dt_inicio_vigencia,
			coalesce(dt_fim_vigencia,clock_timestamp()),
			nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	ie_acao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

nr_seq_regra_p		:= nr_seq_regra_w;
ie_acao_p		:= ie_acao_w;

end;
$body$
