CREATE OR REPLACE PROCEDURE obter_regra_ajuste_partic ( cd_estabelecimento_p bigint, cd_convenio_p bigint, qt_porte_anestesico_p bigint, dt_procedimento_p timestamp, cd_especialidade_p bigint, vl_participante_p INOUT bigint, vl_medico_original_p bigint) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	coalesce(vl_participante,0),
		coalesce(tx_medico_orig_tab,0)
	from	regra_valor_participante a
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_convenio		= cd_convenio_p
	and	coalesce(nr_porte_anestesico,coalesce(qt_porte_anestesico_p,0))	= coalesce(qt_porte_anestesico_p,0)
	and 	coalesce(cd_especialidade, coalesce(cd_especialidade_p,0)) = coalesce(cd_especialidade_p,0)
	and	dt_procedimento_p between dt_inicio_vigencia and coalesce(dt_final_vigencia,dt_procedimento_p)
	order by dt_inicio_vigencia,
		coalesce(cd_especialidade,0);

vl_participante_w		double precision;
tx_medico_orig_tab_w		regra_valor_participante.tx_medico_orig_tab%type;

BEGIN

open C01;
loop
fetch C01 into
	vl_participante_w,
	tx_medico_orig_tab_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (vl_participante_w > 0) then
	vl_participante_p	:= vl_participante_w;
elsif (tx_medico_orig_tab_w > 0) and (coalesce(vl_medico_original_p,0) > 0) then
	vl_participante_p	:= (dividir(vl_medico_original_p,100) * tx_medico_orig_tab_w);
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
