CREATE OR REPLACE PROCEDURE obter_regra_ajuste_terc_oper (cd_estabelecimento_p bigint, nr_seq_operacao_p bigint, nr_seq_terceiro_p bigint, vl_operacao_p INOUT bigint) AS $body$
DECLARE



pr_ajuste_w	double precision;


c01 CURSOR FOR
SELECT	pr_ajuste
FROM	regra_ajuste_terc_oper
WHERE	cd_estabelecimento	= cd_estabelecimento_p
AND	coalesce(nr_seq_terceiro,coalesce(nr_seq_terceiro_p,0))	= coalesce(nr_seq_terceiro_p,0)
AND	coalesce(nr_seq_operacao,coalesce(nr_Seq_operacao_p,0))	= coalesce(nr_Seq_operacao_p,0)
and	ie_situacao = 'A'
ORDER 	BY coalesce(nr_seq_terceiro, 0),
	coalesce(nr_seq_operacao, 0);


BEGIN

pr_ajuste_w	:= 0;
open c01;
loop
fetch c01 into
	pr_ajuste_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

vl_operacao_p		:= vl_operacao_p + ((vl_operacao_p / 100) * pr_ajuste_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
