SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_regra_atualizar_barras ( cd_material_p bigint, cd_estabelecimento_p bigint, ie_gera_p INOUT text) AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_w			smallint;
cd_classe_material_w		integer;
ie_barras_material_w		varchar(1);
nr_regras_w			integer;
cd_unidade_w			varchar(30);
cd_empresa_w			smallint;
ie_tipo_material_w		varchar(3);
nr_seq_familia_w		bigint;

c01 CURSOR FOR
	SELECT	ie_barras_material
	from	Regra_nf_lote_fornec
	where	coalesce(cd_unidade_medida, cd_unidade_w)		= cd_unidade_w
	and	coalesce(cd_grupo_material, cd_grupo_material_w)	= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_w)		= cd_subgrupo_w
	and	coalesce(cd_classe_material, cd_classe_material_w)	= cd_classe_material_w
	and	coalesce(ie_tipo_material, ie_tipo_material_w)		= ie_tipo_material_w
	and	coalesce(cd_material, cd_material_p)			= cd_material_p
	and	coalesce(cd_estab_regra, cd_estabelecimento_p) = cd_estabelecimento_p
	and	((coalesce(nr_seq_familia, nr_seq_familia_w)			= nr_seq_familia_w) or (coalesce(nr_seq_familia::text, '') = ''))
	and (cd_empresa = cd_empresa_w or cd_empresa_w = '')
	order by
		coalesce(cd_material, 0),
		coalesce(cd_classe_material, 0),
		coalesce(cd_subgrupo_material, 0),
		coalesce(cd_grupo_material, 0);


BEGIN

select	coalesce(cd_empresa,'')
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_p;

select	count(*)
into STRICT	nr_regras_w
from	Regra_nf_lote_fornec
where	cd_empresa	= cd_empresa_w
and	coalesce(cd_estab_regra, cd_estabelecimento_p) = cd_estabelecimento_p;

if (nr_regras_w	= 0) then
	ie_barras_material_w	:= 'S';
else
	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		nr_seq_familia
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_w,
		cd_classe_material_w,
		nr_seq_familia_w
	from	estrutura_material_v
	where	cd_material	= cd_material_p;

	select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMC'),1,255) cd_unidade_medida_compra,
		ie_tipo_material
	into STRICT	cd_unidade_w,
		ie_tipo_material_w
	from	material
	where	cd_material = cd_material_p;

	OPEN	c01;
	LOOP
	FETCH	c01 INTO
		ie_barras_material_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	END LOOP;
	CLOSE 	c01;
	end;
END IF;

ie_gera_p	:= ie_barras_material_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
