CREATE OR REPLACE PROCEDURE obter_regra_aviso_validade ( cd_material_p bigint, cd_estabelecimento_p bigint, ie_avisa_p INOUT text, qt_dias_validade_p INOUT bigint, ie_local_estoque_lote_p INOUT text) AS $body$
DECLARE


cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;

ie_local_estoque_lote_w	varchar(1);
ie_avisa_w		varchar(1);
qt_dias_validade_w		bigint;
ie_estoque_lote_w		varchar(1) := 'N';

c01 CURSOR FOR
SELECT	coalesce(ie_avisa_validade_lote,'N') ie_avisa,
	coalesce(ie_local_estoque_lote,'N') ie_local_estoque_lote,
	coalesce(qt_dias_validade,0) qt_dias_validade,
	coalesce(ie_estoque_lote,'N') ie_estoque_lote
from	regra_aviso_val_lote
where	ie_situacao = 'A'
and	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(cd_material, cd_material_p) = cd_material_p
and	coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w) = cd_classe_material_w
order by	cd_material desc,
	cd_classe_material desc,
	cd_subgrupo_material desc,
	cd_grupo_material desc;


BEGIN
select 	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

open c01;
loop
fetch c01 into
	ie_avisa_w,
	ie_local_estoque_lote_w,
	qt_dias_validade_w,
	ie_estoque_lote_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (ie_estoque_lote_w = 'S') then
	begin
	ie_estoque_lote_w := substr(coalesce(obter_se_material_estoque_lote(cd_estabelecimento_p, cd_material_p),'N'),1,1);

	if (ie_estoque_lote_w = 'N') then
		ie_avisa_w := 'N';
	end if;
	end;
end if;

ie_avisa_p		:= coalesce(ie_avisa_w,'N');
qt_dias_validade_p		:= coalesce(qt_dias_validade_w,0);
ie_local_estoque_lote_p	:= coalesce(ie_local_estoque_lote_w,'N');

end;
$body$
