CREATE OR REPLACE PROCEDURE obter_regra_cobranca ( nr_atendimento_p bigint, nr_seq_regra_p INOUT bigint) AS $body$
DECLARE

				 
				 
ie_tipo_atendimento_w	integer;
cd_convenio_w		bigint;
nr_seq_regra_w		bigint;

 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pep_regra_cobranca a, 
		pep_regra_cobr_criterio b 
	where	a.nr_sequencia		= b.nr_seq_regra 
	and	a.ie_situacao		= 'A' 
	and	b.ie_situacao		= 'A' 
	and	coalesce(cd_convenio,cd_convenio_w)			= cd_convenio_w 
	and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w)	= ie_tipo_atendimento_w 
	order by coalesce(cd_convenio,0), 
		 coalesce(ie_tipo_atendimento,0);


BEGIN 
begin 
select	cd_convenio, 
	ie_tipo_Atendimento 
into STRICT	cd_convenio_w, 
	ie_tipo_atendimento_w 
from	atendimento_paciente_v 
where	nr_Atendimento	= nr_atendimento_p;
exception 
	when others then 
	null;
	end;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;
 
 
 
nr_seq_regra_p	:= nr_seq_regra_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
