CREATE OR REPLACE PROCEDURE obter_regra_inserir_protoc_onc (cd_protocolo_p bigint, nr_seq_medicacao_p bigint, ie_incluir_p INOUT text, ie_pasta_p text) AS $body$
DECLARE


ie_incluir_w		varchar(1);
ds_retorno_w		varchar(1) := 'S';

C01 CURSOR FOR
	SELECT	coalesce(ie_permite_incluir,'S')
	from	regra_alt_prot_prescricao
	where	coalesce(cd_protocolo,cd_protocolo_p)	= cd_protocolo_p
	and	coalesce(nr_seq_medicacao,nr_seq_medicacao_p)= nr_seq_medicacao_p
	and	((cd_protocolo IS NOT NULL AND cd_protocolo::text <> '') or (nr_seq_medicacao IS NOT NULL AND nr_seq_medicacao::text <> ''))
	and	 	coalesce(cd_grupo_mat::text, '') = ''
	and		coalesce(cd_subgrupo_mat::text, '') = ''
	and		coalesce(cd_classe_mat::text, '') = ''
	and		coalesce(cd_material::text, '') = ''
	and 	coalesce(ie_pasta,ie_pasta_p) = ie_pasta_p
	order by coalesce(cd_protocolo,0),
		 coalesce(nr_seq_medicacao,0);


BEGIN

if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') or (nr_seq_medicacao_p IS NOT NULL AND nr_seq_medicacao_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		ie_incluir_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ds_retorno_w := ie_incluir_w;
	end loop;
	close C01;

end if;

ie_incluir_p := ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
