CREATE OR REPLACE PROCEDURE obter_regra_just_requisicao ( nr_requisicao_p bigint, cd_material_p bigint, nr_sequencia_p INOUT bigint, ie_obriga_justif_p INOUT text) AS $body$
DECLARE


cd_operacao_estoque_w		smallint;
nr_sequencia_w			bigint;
cd_local_estoque_w		smallint;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_obriga_justif_w		varchar(1):= 'N';
ie_urgente_w			regra_justific_requisicao.ie_urgente%type;

c01 CURSOR FOR
	SELECT	ie_obriga_justif,
		nr_sequencia
	from	regra_justific_requisicao
	where	coalesce(cd_operacao_estoque, cd_operacao_estoque_w)	= cd_operacao_estoque_w
	and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
	and	coalesce(cd_material, cd_material_p)			= cd_material_p
	and	((coalesce(ie_padrao_local, 'A') = 'A') or (coalesce(ie_padrao_local, 'A') =
			obter_se_mat_existe_padrao_loc(cd_material_p, cd_local_estoque_w)))
	and (coalesce(cd_local_estoque, cd_local_estoque_w) = cd_local_estoque_w)
	and (ie_urgente = 'A' or ie_urgente = ie_urgente_w)
	order by	coalesce(cd_local_estoque,0),
			coalesce(cd_material,0),
			coalesce(cd_classe_material,0),
			coalesce(cd_subgrupo_material,0),
			coalesce(cd_grupo_material,0),
			coalesce(cd_operacao_estoque,0);


BEGIN

select	cd_operacao_estoque,
	coalesce(cd_local_estoque_destino,cd_local_estoque),
	ie_urgente
into STRICT	cd_operacao_estoque_w,
	cd_local_estoque_w,
	ie_urgente_w
from	requisicao_material
where	nr_requisicao	= nr_requisicao_p;

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

open c01;
loop
fetch c01 into
	ie_obriga_justif_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ie_obriga_justif_p	:= ie_obriga_justif_w;
	nr_sequencia_p	:= nr_sequencia_w;
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
