CREATE OR REPLACE PROCEDURE obter_regra_nota_credito ( ie_origem_p text, dt_nota_credito_p timestamp, nr_seq_trans_baixa_tit_pagar_p INOUT bigint, nr_seq_trans_fin_contab_p INOUT bigint, dt_vencimento_p INOUT timestamp) AS $body$
DECLARE


nr_seq_regra_w			bigint;

nr_seq_trans_baixa_tit_pagar_w	bigint;
nr_seq_trans_fin_contab_w		bigint;
qt_dias_vencimento_w		integer;
dt_vencimento_w			timestamp;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_seq_trans_baixa_tit_pagar,
	nr_seq_trans_fin_contab,
	qt_dias_vencimento
from	regra_nota_credito
where	coalesce(ie_origem, ie_origem_p) = ie_origem_p
order by ie_origem desc;


BEGIN
open c01;
loop
fetch c01 into
	nr_seq_regra_w,
	nr_seq_trans_baixa_tit_pagar_w,
	nr_seq_trans_fin_contab_w,
	qt_dias_vencimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (qt_dias_vencimento_w > 0) then
	dt_vencimento_w := coalesce(dt_vencimento_p, clock_timestamp()) + qt_dias_vencimento_w;
end if;

nr_seq_trans_baixa_tit_pagar_p	:= coalesce(nr_seq_trans_baixa_tit_pagar_w, nr_seq_trans_baixa_tit_pagar_p);
nr_seq_trans_fin_contab_p		:= coalesce(nr_seq_trans_fin_contab_w, nr_seq_trans_fin_contab_p);
dt_vencimento_p			:= coalesce(dt_vencimento_w, dt_vencimento_p);
end;
