SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_regra_permissao_oc ( nr_ordem_compra_p bigint, cd_material_p bigint, ie_opcao_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


					
/*
ie_opcao_p
	SP - Somente pagamento
*/
					
					
ds_retorno_w			varchar(255);
ie_somente_pagto_w		ordem_compra.ie_somente_pagto%type;
cd_material_w			material.cd_material%type;
cd_grupo_material_w		grupo_material.cd_grupo_material%type;
cd_subgrupo_material_w		subgrupo_material.cd_subgrupo_material%type;
cd_classe_material_w		classe_material.cd_classe_material%type;
ie_permite_somente_pagto_w		regra_permissao_oc.ie_permite_somente_pagto%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;


c01 CURSOR FOR
SELECT	coalesce(ie_permite_somente_pagto,'S')
from	regra_permissao_oc
where	cd_estabelecimento = cd_estabelecimento_w
and	ie_situacao = 'A'
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

select	coalesce(ie_somente_pagto,'N'),
	cd_estabelecimento
into STRICT	ie_somente_pagto_w,
	cd_estabelecimento_w
from	ordem_compra
where	nr_ordem_compra = nr_ordem_compra_p;

if (ie_opcao_p = 'SP') and (ie_somente_pagto_w = 'S') then	
	

	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;
	
	open C01;
	loop
	fetch C01 into	
		ie_permite_somente_pagto_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_permite_somente_pagto_w := ie_permite_somente_pagto_w;
		end;
	end loop;
	close C01;
	
	if (ie_permite_somente_pagto_w = 'N') then
		ds_retorno_w	:= WHEB_MENSAGEM_PCK.get_texto(283977,'CD_MATERIAL_P='||cd_material_p);
	end if;
end if;


ds_retorno_p	:= ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_regra_permissao_oc ( nr_ordem_compra_p bigint, cd_material_p bigint, ie_opcao_p text, ds_retorno_p INOUT text) FROM PUBLIC;

