CREATE OR REPLACE PROCEDURE obter_regra_tabela_sistema ( nm_tabela_p text, nr_seq_visao_p bigint, ds_config_p INOUT text, ds_titulo_p INOUT text) AS $body$
DECLARE


ds_sep_w		varchar(2) := ';';
ds_colunas_w		varchar(256);
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w		integer := wheb_usuario_pck.get_cd_perfil;
cd_funcao_W		bigint := wheb_usuario_pck.get_cd_funcao;
nm_usuario_regra_w	varchar(15) := wheb_usuario_pck.get_nm_usuario;

C01 CURSOR FOR
	SELECT	IE_REGRA,
		DS_TITULO
	FROM	tabela_sistema_regra
	where 	nm_tabela = nm_tabela_p
	  and   coalesce(cd_estabelecimento, cd_estabelecimento_w)	= cd_estabelecimento_w
	  and 	coalesce(cd_perfil, cd_perfil_w)	= cd_perfil_w
	  and 	coalesce(cd_funcao, cd_funcao_w)	= cd_funcao_w
	  and 	coalesce(nr_seq_visao, nr_seq_visao_p)  = nr_seq_visao_p
	  and 	coalesce(nm_usuario_regra, nm_usuario_regra_w) = nm_usuario_regra_w
	ORDER BY	coalesce(nm_usuario_regra,'AAA'),
			coalesce(cd_funcao,0),
			coalesce(cd_perfil,0),
			coalesce(cd_estabelecimento,0),
			coalesce(nr_seq_visao,0);
	

BEGIN
	ds_config_p := NULL;
	OPEN C01;
	LOOP
	FETCH C01 INTO
		ds_config_p,
		ds_titulo_p;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		BEGIN
			ds_config_p := ds_config_p;
			ds_titulo_p := ds_titulo_p;
			
		END;
	END LOOP;
	CLOSE C01;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
