CREATE OR REPLACE PROCEDURE obter_regra_trans_tipo_receb ( cd_tipo_recebimento_p bigint, ie_receb_cheque_p INOUT text, ie_receb_cartao_p INOUT text, nr_seq_trans_financ_p INOUT bigint) AS $body$
DECLARE



nr_seq_trans_financ_w	funcao_tf_trans_financ.nr_seq_trans_financ%type;
nr_seq_tf_campo_w	funcao_tf_regra_receb.nr_seq_tf_campo%type;
ie_receb_cheque_w	transacao_financeira.ie_receb_cheque%type;
ie_receb_cartao_w	transacao_financeira.ie_receb_cartao%type;


BEGIN

ie_receb_cheque_w := 'S';
ie_receb_cartao_w := 'S';

select	max(nr_seq_tf_campo)
into STRICT	nr_seq_tf_campo_w
from	funcao_tf_regra_receb
where	cd_tipo_recebimento	= cd_tipo_recebimento_p;

if (nr_seq_tf_campo_w IS NOT NULL AND nr_seq_tf_campo_w::text <> '') then

	select	max(nr_seq_trans_financ)
	into STRICT	nr_seq_trans_financ_w
	from	funcao_tf_trans_financ
	where	nr_seq_tf_campo	= nr_seq_tf_campo_w;

	if (nr_seq_trans_financ_w IS NOT NULL AND nr_seq_trans_financ_w::text <> '') then

		select	coalesce(max(ie_receb_cheque),'S') ie_receb_cheque,
			coalesce(max(ie_receb_cartao),'S') ie_receb_cartao
		into STRICT	ie_receb_cheque_w,
			ie_receb_cartao_w
		from	transacao_financeira
		where	nr_sequencia	= nr_seq_trans_financ_w;


	end if;

end if;

ie_receb_cheque_p := ie_receb_cheque_w;
ie_receb_cartao_p := ie_receb_cartao_w;
nr_seq_trans_financ_p := nr_seq_trans_financ_w;

end;
$body$
