SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_regra_uso_mat ( nr_atendimento_p bigint, cd_material_p bigint, qt_material_p bigint, cd_setor_atendimento_p bigint, ie_acao_excesso_p INOUT text, qt_excedida_p INOUT text, nr_seq_regra_p INOUT bigint, ds_mensagem_p INOUT text, cd_categoria_p text, cd_plano_p text, cd_cgc_fornecedor_p text, cd_cgc_prestador_p text, dt_atendimento_p timestamp, nr_seq_mat_p bigint default null, nr_interno_conta_p bigint default null) AS $body$
DECLARE

				
				
ds_retorno_w		varchar(255);
ie_acao_excesso_w	varchar(5);
ds_erro_w		varchar(255);
qt_registros_w		integer;
ie_tipo_atendimento_w	smallint;
cd_convenio_w		integer;
ie_tipo_qtde_w		varchar(1);
qt_permitida_w		double precision;
qt_horas_intervalo_w	integer;
qt_executada_w		double precision;
dt_material_w		timestamp;
ds_material_w		varchar(255);
qt_executada_dia_w	double precision;
qt_executada_mes_w	double precision;
qt_executada_atend_w	double precision;
qt_executada_periodo_w	double precision;
qt_executada_conta_w	double precision	:= 0;
qt_executada_conta_ww	double precision	:= 0;
qt_exec_total_conta_w	double precision	:= 0;
qt_excedida_w		double precision	:= 0;
nr_seq_regra_uso_w	bigint;
cd_area_proc_w		bigint;
cd_especialidade_proc_w	bigint;
cd_grupo_proc_w		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_grupo_w		smallint	:= 0;
cd_subgrupo_w		smallint	:= 0;
cd_classe_w		integer	:= 0;
cd_estabelecimento_w	smallint;
qt_minutos_intervalo_w	bigint;
qt_registro_periodo_w	bigint	:= 0;
cd_tipo_acomodacao_w	smallint;
ds_mensagem_w		varchar(255);
cd_pessoa_fisica_w	varchar(10);
qt_idade_w		double precision;
ie_paciente_isolado_w	varchar(1);
nr_interno_conta_w	material_atend_paciente.nr_interno_conta%type;

c01 CURSOR FOR
	SELECT	a.qt_permitida,
		a.ie_acao_excesso,
		a.qt_horas_intervalo,
		a.ie_tipo_qtde,
		a.nr_sequencia,
		a.qt_minutos_intervalo,
		a.ds_mensagem
      	from 	convenio_regra_qtde_mat	a
	where	a.cd_convenio				= cd_convenio_w
	and	coalesce(cd_material,cd_material_p)		= cd_material_p
	and	coalesce(cd_material,cd_material_p)		= cd_material_p
	and 	coalesce(cd_classe_material, cd_classe_w)	= cd_classe_w
	and 	coalesce(cd_subgrupo_material, cd_subgrupo_w) = cd_subgrupo_w
	and 	coalesce(cd_grupo_material, cd_grupo_w) 	 = cd_grupo_w
	and	coalesce(a.ie_tipo_atendimento,ie_tipo_atendimento_w) = ie_tipo_atendimento_w
      	and	coalesce(a.cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and	coalesce(a.cd_categoria,coalesce(cd_categoria_p,0)) = 	coalesce(cd_categoria_p,0)
	and	coalesce(a.cd_plano,coalesce(cd_plano_p,0)) = coalesce(cd_plano_p,0)
	and	coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_w,0)) = coalesce(cd_estabelecimento_w,0)
	and	coalesce(cd_tipo_acomodacao, coalesce(cd_tipo_acomodacao_w,0)) = coalesce(cd_tipo_acomodacao_w,0)
	and	coalesce(qt_idade_w,1) between coalesce(obter_idade_regra_uso(a.nr_sequencia,'MIN','M'),0) and coalesce(obter_idade_regra_uso(a.nr_sequencia,'MAX','M'),9999999)
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(cd_perfil, coalesce(obter_perfil_ativo, 0)) = coalesce(obter_perfil_ativo, 0)
	and	coalesce(a.cd_cgc_fornecedor, coalesce(cd_cgc_fornecedor_p,'0')) = coalesce(cd_cgc_fornecedor_p,'0')
	and	coalesce(a.cd_cgc_prestador, coalesce(cd_cgc_prestador_p,'0')) = coalesce(cd_cgc_prestador_p,'0')
	and ((coalesce(a.ie_paciente_isolado,'A') = 'A') or (coalesce(a.ie_paciente_isolado,'A') = ie_paciente_isolado_w))
	order by
		coalesce(cd_material,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(a.cd_cgc_fornecedor,'0'),
		coalesce(a.cd_cgc_prestador,'0'),
		coalesce(a.ie_tipo_atendimento,'0'),
		coalesce(a.cd_setor_atendimento,0),
		coalesce(a.cd_estabelecimento,0),
		coalesce(cd_perfil,0);


BEGIN

/*select	count(*)
into	qt_registros_w
from	convenio_regra_qtde_mat
where	cd_material	= cd_material_p;*/
select	coalesce(max(obter_convenio_atendimento(nr_atendimento_p)),0),
	coalesce(max((obter_tipo_acomod_atend(nr_atendimento_p,'C'))::numeric ),0)
into STRICT	cd_convenio_w,
	cd_tipo_acomodacao_w
;
	
select	coalesce(max(a.cd_classe_material),0),
	coalesce(max(b.cd_subgrupo_material),0),
	coalesce(max(c.cd_grupo_material),0)
into STRICT	cd_classe_w,
	cd_subgrupo_w,
	cd_grupo_w
from	material a,
	classe_material b,
	subgrupo_material c
where	a.cd_material          = cd_material_p
and	a.cd_classe_material   = b.cd_classe_material
and	b.cd_subgrupo_material = c.cd_subgrupo_material;

begin
select 	coalesce(ie_tipo_atendimento,0),
	coalesce(cd_estabelecimento,0),
	cd_pessoa_fisica,
	coalesce(ie_paciente_isolado,'A')
into STRICT	ie_tipo_atendimento_w,
	cd_estabelecimento_w,
	cd_pessoa_fisica_w,
	ie_paciente_isolado_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;
exception
	when others then
		ie_tipo_qtde_w  :=  'X';
end;

begin
select	max(obter_idade(b.dt_nascimento,coalesce(b.dt_obito,clock_timestamp()),'DIA'))	
into STRICT	qt_idade_w	
from	pessoa_fisica b
where	b.cd_pessoa_fisica	= cd_pessoa_fisica_w;
exception
when others then
	qt_idade_w	:= 0;
end;

select	count(*)
into STRICT	qt_registros_w
from 	convenio_regra_qtde_mat
where	cd_convenio				= cd_convenio_w
and	coalesce(cd_material,cd_material_p)		= cd_material_p
and 	coalesce(cd_classe_material, cd_classe_w)	= cd_classe_w
and 	coalesce(cd_subgrupo_material, cd_subgrupo_w) = cd_subgrupo_w
and 	coalesce(cd_grupo_material, cd_grupo_w) 	 = cd_grupo_w
and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w) = ie_tipo_atendimento_w
and	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
and	coalesce(cd_categoria,coalesce(cd_categoria_p,0)) = 	coalesce(cd_categoria_p,0)
and	coalesce(cd_plano,coalesce(cd_plano_p,0)) = coalesce(cd_plano_p,0)
and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_w,0)) = coalesce(cd_estabelecimento_w,0)
and	coalesce(ie_situacao,'A') = 'A';

if (qt_registros_w	> 0) then
	
	OPEN C01;
	LOOP
	FETCH C01 into
		qt_permitida_w,
		ie_acao_excesso_w,
		qt_horas_intervalo_w,
		ie_tipo_qtde_w,
		nr_seq_regra_uso_w,
		qt_minutos_intervalo_w,
		ds_mensagem_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		qt_permitida_w		:= qt_permitida_w;
		ie_acao_excesso_w	:= ie_acao_excesso_w;
		qt_horas_intervalo_w	:= qt_horas_intervalo_w;
		ie_tipo_qtde_w		:= ie_tipo_qtde_w;
		nr_seq_regra_uso_w	:= nr_seq_regra_uso_w;
		qt_minutos_intervalo_w	:= qt_minutos_intervalo_w;
		end;
	END LOOP;
	CLOSE C01;
	
	dt_material_w	:= coalesce(dt_atendimento_p,clock_timestamp());
	
	if (ie_tipo_qtde_w = 'D') then

		select 	coalesce(sum(qt_material),0)
		into STRICT	qt_executada_dia_w
		from	material_atend_paciente
		where	nr_atendimento		= nr_atendimento_p
		and	cd_material		= cd_material_p
		and	(nr_interno_conta IS NOT NULL AND nr_interno_conta::text <> '')
		and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_atendimento)	= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_material_w)
		and	coalesce(cd_motivo_exc_conta::text, '') = ''
		and 	nr_sequencia <> coalesce(nr_seq_mat_p,0);
		

		if	((qt_executada_dia_w + qt_material_p)	> qt_permitida_w) then
			qt_excedida_w	:= (qt_executada_dia_w + qt_material_p) - qt_permitida_w;
			ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(308997);
				
		end if;
		
	end if;

	if (ie_tipo_qtde_w = 'M') then

		select 	coalesce(sum(qt_material),0)
		into STRICT	qt_executada_mes_w
		from	material_atend_paciente
		where	nr_atendimento		= nr_atendimento_p
		and	cd_material		= cd_material_p
		and	(nr_interno_conta IS NOT NULL AND nr_interno_conta::text <> '')
		and	PKG_DATE_UTILS.START_OF(dt_atendimento, 'month')	= PKG_DATE_UTILS.START_OF(dt_material_w, 'month')
		and	coalesce(cd_motivo_exc_conta::text, '') = ''
		and 	nr_sequencia <> coalesce(nr_seq_mat_p,0);

		if	((qt_executada_mes_w + qt_material_p)  > qt_permitida_w) then
			qt_excedida_w	:= (qt_executada_mes_w + qt_material_p) - qt_permitida_w;
			ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(308998);
			
		end if;
	end if;

	if (ie_tipo_qtde_w = 'A') then

		select 	coalesce(sum(qt_material),0)
		into STRICT	qt_executada_atend_w
		from	material_atend_paciente
		where	nr_atendimento		= nr_atendimento_p
		and	cd_material		= cd_material_p
		and	coalesce(cd_motivo_exc_conta::text, '') = ''
		and 	nr_sequencia <> coalesce(nr_seq_mat_p,0);

		if	((qt_executada_atend_w + qt_material_p) > qt_permitida_w ) then

			qt_excedida_w	:= (qt_executada_atend_w + qt_material_p) - qt_permitida_w;
			ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(308999);
			
		end if;
	end if;

	if (ie_tipo_qtde_w = 'H') then

		select 	coalesce(sum(qt_material),0)
		into STRICT	qt_executada_periodo_w
		from	material_atend_paciente
		where	nr_atendimento		= nr_atendimento_p
		and	cd_material		= cd_material_p
		and	dt_atendimento between
			(dt_material_w - (qt_horas_intervalo_w * 60 /1440)) and dt_material_w
		and	coalesce(cd_motivo_exc_conta::text, '') = ''
		and 	nr_sequencia <> coalesce(nr_seq_mat_p,0);

		if	((qt_executada_periodo_w + qt_material_p) > qt_permitida_w) then
			qt_excedida_w	:= (qt_executada_periodo_w + qt_material_p)  - qt_permitida_w;
			ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(309000);
			
		end if;
	end if;
	
	if (ie_tipo_qtde_w = 'B') then

		select 	coalesce(sum(qt_material),0)
		into STRICT	qt_executada_periodo_w
		from	material_atend_paciente
		where	nr_atendimento		= nr_atendimento_p
		and	cd_material		= cd_material_p
		and	dt_atendimento between
			(dt_material_w - (qt_minutos_intervalo_w / 1440)) and dt_material_w
		and	coalesce(cd_motivo_exc_conta::text, '') = ''
		and 	nr_sequencia <> coalesce(nr_seq_mat_p,0);

		if	((qt_executada_periodo_w + qt_material_p) > qt_permitida_w) then
			qt_excedida_w	:= (qt_executada_periodo_w + qt_material_p)  - qt_permitida_w;
			ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(309000);
			
		end if;
	end if;
	
	if (ie_tipo_qtde_w = 'R') then

		select 	count(*)
		into STRICT	qt_registro_periodo_w
		from	material_atend_paciente
		where	nr_atendimento		= nr_atendimento_p
		and	cd_material		= cd_material_p
		and	dt_atendimento between
			(dt_material_w - (qt_minutos_intervalo_w / 1440)) and dt_material_w
		and	coalesce(cd_motivo_exc_conta::text, '') = '';

		if (qt_registro_periodo_w >= qt_permitida_w) then
			qt_excedida_w	:= (qt_registro_periodo_w + 1) - qt_permitida_w;
			ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(309001);
				
		end if;
	end if;

	if (ie_tipo_qtde_w = 'S') then

		select 	coalesce(sum(qt_material),0)
		into STRICT	qt_executada_atend_w
		from	material_atend_paciente
		where	nr_atendimento		= nr_atendimento_p
		and	cd_material		= cd_material_p
		and	cd_setor_atendimento	= cd_setor_atendimento_p
		and	coalesce(cd_motivo_exc_conta::text, '') = ''
		and 	nr_sequencia <> coalesce(nr_seq_mat_p,0);

		if	((qt_executada_atend_w + qt_material_p) > qt_permitida_w ) then
			qt_excedida_w	:= (qt_executada_atend_w + qt_material_p) - qt_permitida_w;
			ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(309002);
			
		end if;
	end if;

	if (ie_tipo_qtde_w = 'C') then
    if (coalesce(nr_interno_conta_p,0) > 0) then 		
			select 	coalesce(sum(qt_material),0)
			into STRICT	qt_executada_conta_ww
			from	material_atend_paciente
			where	nr_atendimento		= nr_atendimento_p
			and	cd_material		= cd_material_p
			and     nr_interno_conta	= nr_interno_conta_p
			and	coalesce(cd_motivo_exc_conta::text, '') = ''
			and 	nr_sequencia <> coalesce(nr_seq_mat_p,0);
		else 	
			select 	coalesce(sum(qt_material),0)
			into STRICT	qt_executada_conta_ww
			from	material_atend_paciente
			where	nr_atendimento		= nr_atendimento_p
			and	cd_material		= cd_material_p
			and	(nr_interno_conta IS NOT NULL AND nr_interno_conta::text <> '')
			and	coalesce(cd_motivo_exc_conta::text, '') = ''
			and 	nr_sequencia <> coalesce(nr_seq_mat_p,0);
		end if;

		if	((qt_executada_conta_ww + qt_material_p)	> qt_permitida_w) then
			qt_excedida_w	:= (qt_executada_conta_ww + qt_material_p) - qt_permitida_w;
			ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(309003);

		end if;
		
	end if;	

	
	if (ie_tipo_qtde_w		= 'L') and (qt_material_p		> qt_permitida_w) then
		qt_excedida_w	:= (qt_material_p) - qt_permitida_w;
		ds_erro_w 	:= WHEB_MENSAGEM_PCK.get_texto(309004);		
		
	end if;	
	
end if;

if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	ie_acao_excesso_p	:= ie_acao_excesso_w;
	qt_excedida_p		:= qt_excedida_w;
	nr_seq_regra_p		:= nr_seq_regra_uso_w;
	if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then
		ds_erro_w := ds_mensagem_w;
	end if;
	ds_mensagem_p		:= ds_erro_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_regra_uso_mat ( nr_atendimento_p bigint, cd_material_p bigint, qt_material_p bigint, cd_setor_atendimento_p bigint, ie_acao_excesso_p INOUT text, qt_excedida_p INOUT text, nr_seq_regra_p INOUT bigint, ds_mensagem_p INOUT text, cd_categoria_p text, cd_plano_p text, cd_cgc_fornecedor_p text, cd_cgc_prestador_p text, dt_atendimento_p timestamp, nr_seq_mat_p bigint default null, nr_interno_conta_p bigint default null) FROM PUBLIC;

