CREATE OR REPLACE PROCEDURE obter_remessa_escritural ( nr_seq_conta_p bigint, nr_remessa_p INOUT bigint ) AS $body$
DECLARE

									 
nr_remessa_cobr_w	banco_estabelecimento.nr_remessa_cobr%type;


BEGIN

select	max(a.nr_remessa_cobr)
into STRICT	nr_remessa_cobr_w
from	banco_estabelecimento a
where	a.nr_sequencia = nr_seq_conta_p;

if (nr_remessa_cobr_w IS NOT NULL AND nr_remessa_cobr_w::text <> '') then

	nr_remessa_cobr_w := nr_remessa_cobr_w + 1;

	update	banco_estabelecimento
	set		nr_remessa_cobr = nr_remessa_cobr_w
	where	nr_sequencia	= nr_seq_conta_p;

	commit;
	
	nr_remessa_p := nr_remessa_cobr_w;
	
else

	nr_remessa_p := null;

end if;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
