CREATE OR REPLACE PROCEDURE obter_restri_prot_cirurgia ( nr_cirurgia_p bigint, nr_seq_protocolo_p bigint, nr_seq_pepo_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE


qt_cirurgia_w			integer;
qt_cirurgia_protocolo_w	integer;


BEGIN

select	count(*)
into STRICT	qt_cirurgia_w
from	cirurgia_protocolo_util
where	((nr_cirurgia	=	nr_cirurgia_p) or (nr_seq_pepo = nr_seq_pepo_p));

if (qt_cirurgia_w > 0) then
	select	count(*)
	into STRICT	qt_cirurgia_protocolo_w
	from	cirurgia_protocolo_util
	where	((nr_cirurgia	=	nr_cirurgia_p) or (nr_seq_pepo = nr_seq_pepo_p))
	and	nr_seq_protocolo = 	nr_seq_protocolo_p;
end if;

if (qt_cirurgia_protocolo_w > 0) then
	ds_mensagem_p	:= 	wheb_mensagem_pck.get_texto(299958);
elsif (qt_cirurgia_w > 0) then
	ds_mensagem_p	:= 	wheb_mensagem_pck.get_texto(299959);
end if;

end;
$body$
