CREATE OR REPLACE PROCEDURE obter_saldo_titulo_proj_rec ( vl_pago_p bigint, vl_descontos_p bigint, vl_devolucao_p bigint, vl_glosa_p bigint, vl_ir_p bigint, vl_juros_p bigint, vl_multa_p bigint, vl_outras_deduc_p bigint, vl_outros_acresc_p bigint, vl_tit_saldo_proj_rec_p INOUT bigint) AS $body$
DECLARE


qt_existe_w		bigint;
vl_transacao_w		double precision;
ie_campo_valor_w		varchar(15);

c01 CURSOR FOR
SELECT	ie_campo_valor
from	regra_proj_rec_tit_pag
group by	ie_campo_valor;


BEGIN

vl_transacao_w	:= vl_pago_p;

select	count(*)
into STRICT	qt_existe_w
from	regra_proj_rec_tit_pag;

if (qt_existe_w > 0) and (vl_transacao_w > 0) then
	begin

	open c01;
	loop
	fetch c01 into
		ie_campo_valor_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (ie_campo_valor_w = 'VL_DESCONTOS') then
			vl_transacao_w	:= vl_transacao_w	+ vl_descontos_p;

		elsif (ie_campo_valor_w = 'VL_DEVOLUCAO') then
			vl_transacao_w	:= vl_transacao_w	- vl_devolucao_p;

		elsif (ie_campo_valor_w = 'VL_GLOSA') then
			vl_transacao_w	:= vl_transacao_w	+ vl_glosa_p;

		elsif (ie_campo_valor_w = 'VL_IR') then
			vl_transacao_w	:= vl_transacao_w	+ vl_ir_p;

		elsif (ie_campo_valor_w = 'VL_JUROS') then
			vl_transacao_w	:= vl_transacao_w	- vl_juros_p;

		elsif (ie_campo_valor_w = 'VL_MULTA') then
			vl_transacao_w	:= vl_transacao_w	- vl_multa_p;

		elsif (ie_campo_valor_w = 'VL_OUTRAS_DEDUC') then
			vl_transacao_w	:= vl_transacao_w	+ vl_outras_deduc_p;

		elsif (ie_campo_valor_w = 'VL_OUTROS_ACRES') then
			vl_transacao_w	:= vl_transacao_w	- vl_outros_acresc_p;
		end if;

		end;
	end loop;
	close c01;

	end;
end if;

vl_tit_saldo_proj_rec_p	:= vl_transacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
