SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_schematic4test_updating (SCHED_UPDATING_P INOUT bigint) AS $body$
DECLARE

SCHED_UPDATING_W smallint;
QTD_W bigint;


BEGIN
  SCHED_UPDATING_W := 0;

  --procedure that verify if has package ci cd updating
  SELECT COUNT(NR_SEQUENCIA) QTD
       INTO STRICT QTD_W
  FROM SCHEM_TEST_PACKAGE_SCHED packsched 
  WHERE packsched.DT_SCHEDULE < clock_timestamp() AND coalesce(packsched.DT_EXECUTION::text, '') = ''
  AND packsched.IE_STATUS = '10'
  AND packsched.NM_USUARIO_NREC LIKE 'Dev-NightBuild'
  ORDER BY packsched.DT_SCHEDULE asc;

  IF (QTD_W <> 0) THEN
    SCHED_UPDATING_W := 1;
  END IF;

  SCHED_UPDATING_P := SCHED_UPDATING_W;
  EXCEPTION
  WHEN no_data_found THEN
    SCHED_UPDATING_P := 0;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_schematic4test_updating (SCHED_UPDATING_P INOUT bigint) FROM PUBLIC;

