CREATE OR REPLACE PROCEDURE obter_score_esc_child_pugh_md ( ie_bilirrubina_p bigint, ie_albumina_p bigint, ie_inr_p bigint, ie_ascite_p bigint, ie_quick_p bigint, ie_encefalopatia_hepatica_p bigint, qt_pontuacao_p INOUT bigint, pr_sobrevida1_p INOUT bigint, pr_sobrevida2_p INOUT bigint ) AS $body$
