CREATE OR REPLACE PROCEDURE obter_seq_regra_ie_acao_js ( nr_sequencia_p bigint, ie_acao_p INOUT bigint, nr_seq_regra_p INOUT bigint, ds_caption_p INOUT text) AS $body$
DECLARE


nr_tag_w	smallint;


BEGIN
	select	coalesce(max(ie_acao),1) ie_acao
	into STRICT	ie_acao_p
	from	transacao_financeira
	where	nr_sequencia = nr_sequencia_p;

	if (ie_acao_p = 0) then
		nr_tag_w 	:= 3;
		ds_caption_p 	:= obter_texto_tasy(96684, wheb_usuario_pck.get_nr_seq_idioma);
	elsif (ie_acao_p = 1 or ie_acao_p = 18) then
		nr_tag_w	:= 1;
		ds_caption_p 	:= obter_texto_tasy(96685, wheb_usuario_pck.get_nr_seq_idioma);
	elsif (ie_acao_p = 3) then
		nr_tag_w	:= 2;
		ds_caption_p 	:= obter_texto_tasy(96686, wheb_usuario_pck.get_nr_seq_idioma);
	end if;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_regra_p
	from	funcao_regra
	where	cd_funcao	= 813
	and	nr_tag		= nr_tag_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
