CREATE OR REPLACE PROCEDURE obter_se_acesso_setor_pep ( nr_atendimento_p bigint, nm_usuario_p text, ie_retorno_p INOUT text) AS $body$
DECLARE

 
dt_alta_w		varchar(20);
qt_setor_w		bigint;


BEGIN 
select	max(dt_alta) 
into STRICT	dt_alta_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
if (dt_alta_w = '') then 
	select	count(*) 
	into STRICT	qt_setor_w 
	from	atend_paciente_unidade 
	where	nr_atendimento	= nr_atendimento_p 
	and		dt_saida_interno = to_date('30/12/2999','dd/mm/yyyy') 
	and		obter_se_setor_usuario(cd_setor_atendimento,nm_usuario_p) = 'S';
else 
	select	count(*) 
	into STRICT	qt_setor_w 
	from	atend_paciente_unidade 
	where	nr_atendimento	= nr_atendimento_p 
	and		dt_saida_interno = to_date('30/12/2999','dd/mm/yyyy') 
	and		obter_se_setor_usuario(obter_setor_atendimento(nr_atendimento_p),nm_usuario_p) = 'S';
end if;
 
if (qt_setor_w > 0) then 
	ie_retorno_p := 'S';
else 
	ie_retorno_p := 'N';
end if;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
