CREATE OR REPLACE PROCEDURE obter_se_alerta_pf_usuario_js ( ie_buscar_pf_p text, nr_seq_tratamento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, cd_paciente_p text, cd_pf_destino_p text, ie_acao_p text, cd_paciente_rxt_p INOUT text, ie_alerta_p INOUT text) AS $body$
DECLARE


cd_paciente_w	varchar(10);


BEGIN

if (ie_buscar_pf_p = 'S') then
	begin
	select	a.cd_pessoa_fisica
	into STRICT	cd_paciente_w
	from	rxt_tumor a,
		rxt_tratamento b
	where	b.nr_seq_tumor	= a.nr_sequencia
	and	b.nr_sequencia	= nr_seq_tratamento_p;

	cd_paciente_rxt_p	:= cd_paciente_w;
	end;
else
	cd_paciente_w	:= cd_paciente_p;
end if;

ie_alerta_p	:= obter_se_alerta_pf_usuario(dt_inicial_p, dt_final_p, cd_paciente_w, cd_pf_destino_p, 'S', ie_acao_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
