CREATE OR REPLACE PROCEDURE obter_se_consiste_estoque_mat (cd_material_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_consistir_p INOUT text, cd_kit_material_p bigint) AS $body$
DECLARE


cd_grupo_material_w			bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
ie_atualiza_estoque_w		varchar(200);
cd_perfil_w					integer;

c01 CURSOR FOR
SELECT	b.ie_atualiza_estoque
from	regra_exec_lib_prescr_mat a,
		tipo_baixa_prescricao b
where	b.nr_sequencia	= a.CD_TIPO_BAIXA
and	coalesce(cd_grupo_material, coalesce(cd_grupo_material_w,0))		= coalesce(cd_grupo_material_w,0)
and	coalesce(cd_subgrupo_material, coalesce(cd_subgrupo_material_w,0))	= coalesce(cd_subgrupo_material_w,0)
and	coalesce(cd_kit_material, coalesce(cd_kit_material_p,0))		= coalesce(cd_kit_material_p,0)
and	coalesce(cd_classe_material, coalesce(cd_classe_material_w,0))		= coalesce(cd_classe_material_w,0)
and	coalesce(cd_material, coalesce(cd_material_p,0))				= coalesce(cd_material_p,0)
and (coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p, 0))	= coalesce(cd_setor_atendimento_p,0) and (not exists (	select	1
					from	rep_excluir_mat_conta
					where	cd_setor_atendimento = cd_setor_atendimento_p)))
and	cd_estabelecimento							 = cd_estabelecimento_p
and	coalesce(cd_convenio, coalesce(cd_convenio_p,0))		 = coalesce(cd_convenio_p,0)
and	coalesce(cd_perfil, coalesce(cd_perfil_w,0))			 = coalesce(cd_perfil_w,0)
and	((coalesce(nr_seq_estrutura::text, '') = '') or (consistir_se_mat_estrutura(nr_seq_estrutura,cd_material_p) = 'S'))
order by coalesce(cd_material, 0),
	 coalesce(cd_classe_material, 0),
	 coalesce(cd_subgrupo_material, 0),
	 coalesce(cd_grupo_material, 0),
	coalesce(nr_seq_estrutura,0),
	coalesce(cd_kit_material,0);


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_MATERIAL_v
where	cd_material	= cd_material_p;

select	obter_perfil_ativo
into STRICT	cd_perfil_w
;

ie_atualiza_estoque_w	:= 'S';

open c01;
loop
fetch c01 into
	ie_atualiza_estoque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_atualiza_estoque_w	:= ie_atualiza_estoque_w;
end loop;
close c01;

ie_consistir_p	:= ie_atualiza_estoque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
