CREATE OR REPLACE PROCEDURE obter_se_conta_pac_audit (nr_atendimento_p bigint) AS $body$
DECLARE

 
qt_auditoria_w	bigint;
dt_periodo_final_w	timestamp;
dt_atual_w	timestamp;


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	max(a.dt_periodo_final), 
		clock_timestamp(), 
		count(*) 
	into STRICT	dt_periodo_final_w, 
		dt_atual_w, 
		qt_auditoria_w 
	from	auditoria_conta_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.nr_atendimento	= nr_atendimento_p 
	and	((coalesce(a.dt_periodo_final::text, '') = '') or (a.dt_periodo_final > clock_timestamp()));
 
end if;
 
if (qt_auditoria_w > 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282080);
end if;
 
end;
