CREATE OR REPLACE PROCEDURE obter_se_data_sv_apap ( dt_sinal_vital_p timestamp, nr_apap_p bigint, vl_parametro_p bigint ) AS $body$
DECLARE


    qt_diferenca_w  bigint;
    dt_apap_w       timestamp;
    sql_w           varchar(250);
    nr_hora_apap_w	bigint;


BEGIN
    BEGIN
        nr_hora_apap_w	:= to_number(to_char(round(dt_sinal_vital_p +(vl_parametro_p / 24),'hh24'),'hh24'));
        sql_w := 'CALL OBTER_QT_DIFERENCA_MD (:1, :2, :3) INTO :qt_diferenca_w';
        EXECUTE sql_w
            USING IN dt_sinal_vital_p, IN nr_apap_p, IN dt_apap_w, OUT qt_diferenca_w;

    EXCEPTION
        WHEN OTHERS THEN
            qt_diferenca_w := NULL;
    END;

    IF ( (qt_diferenca_w IS NOT NULL AND qt_diferenca_w::text <> '') AND qt_diferenca_w > vl_parametro_p ) THEN
        CALL wheb_mensagem_pck.exibir_mensagem_abort(246786, 'NR_HORA_APAP_W=' || nr_hora_apap_w);
    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
