CREATE OR REPLACE PROCEDURE obter_se_excedeu_entrada_real ( nr_atendimento_p bigint, nr_sequencia_p bigint, ie_ultrapasso_limite_p INOUT text) AS $body$
DECLARE

 
qt_consiste_entrada_real_w	bigint;			
cd_setor_atendimento_w		bigint;
cd_unidade_basica_w		varchar(10);
cd_unidade_compl_w		varchar(10);
qt_max_visitante_un		bigint;
						

BEGIN 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	max(cd_setor_atendimento), 
		max(cd_unidade_basica), 
		max(cd_unidade_compl) 
	into STRICT	cd_setor_atendimento_w, 
		cd_unidade_basica_w, 
		cd_unidade_compl_w 
	from	atend_paciente_unidade 
	where	nr_seq_interno = obter_atepacu_paciente(nr_atendimento_p,'A');
 
	select	max(qt_max_visitante) 
	into STRICT	qt_max_visitante_un 
	from	unidade_atendimento 
	where	cd_setor_Atendimento = cd_setor_Atendimento_w 
	and	cd_unidade_basica = cd_unidade_basica_w 
	and	cd_unidade_compl = cd_unidade_compl_w;
	 
	select	count(*) 
	into STRICT	qt_consiste_entrada_real_w 
	from	atendimento_visita 
	where	nr_atendimento = nr_atendimento_p 
	and (nr_sequencia != nr_sequencia_p or coalesce(nr_sequencia_p::text, '') = '') 
	and	(dt_entrada_real IS NOT NULL AND dt_entrada_real::text <> '') 
	and	coalesce(dt_saida::text, '') = '';
	 
	if ((qt_consiste_entrada_real_w + 1) > qt_max_visitante_un) then		 
		ie_ultrapasso_limite_p := 'S';
	else 
		ie_ultrapasso_limite_p := 'N';
	end if;
else 
	ie_ultrapasso_limite_p := 'N';
end if;
 
end;
