SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_se_exige_desc_conta ( nr_interno_conta_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

			 
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
cd_plano_w			varchar(10);
ie_tipo_atendimento_w		smallint;
cd_estabelecimento_w		smallint;
ie_exige_desconto_conta_w	varchar(1);
qt_desconto_w			bigint;
qt_exige_desc_w			bigint;
ds_erro_w			varchar(2000)	:= '';


BEGIN 
 
select	max(a.cd_convenio_parametro), 
	max(a.cd_categoria_parametro), 
	max(a.cd_estabelecimento), 
	coalesce(max(b.cd_plano_convenio),'0'), 
	max(c.ie_tipo_atendimento) 
into STRICT	cd_convenio_w, 
	cd_categoria_w, 
	cd_estabelecimento_w, 
	cd_plano_w, 
	ie_tipo_atendimento_w 
from	conta_paciente a, 
	atend_categoria_convenio b, 
	atendimento_paciente c 
where	a.nr_atendimento	= c.nr_atendimento 
and	c.nr_atendimento	= b.nr_atendimento 
and	a.nr_interno_conta	= nr_interno_conta_p;
 
select coalesce(max(ie_exige_desconto_conta),'N') 
into STRICT	ie_exige_desconto_conta_w 
from 	convenio_estabelecimento 
where 	cd_convenio 		= cd_convenio_w 
and 	cd_estabelecimento 	= cd_estabelecimento_w;
 
select	count(1) 
into STRICT	qt_desconto_w 
from 	conta_paciente_desconto 
where 	nr_interno_conta 	= nr_interno_conta_p  LIMIT 1;
 
if	((ie_exige_desconto_conta_w = 'S') or 
	(ie_exige_desconto_conta_w = 'I' AND ie_tipo_atendimento_w = 1)) and (qt_desconto_w = 0) then 
	ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(277658);
	 
elsif (ie_exige_desconto_conta_w = 'R') and (qt_desconto_w = 0) then 
 
	select	count(1) 
	into STRICT	qt_exige_desc_w 
	from	regra_exige_desc_conta 
	where	cd_convenio 					= cd_convenio_w 
	and	ie_situacao					= 'A' 
	and	coalesce(cd_categoria, cd_categoria_w)		= cd_categoria_w 
	and	coalesce(cd_plano, cd_plano_w)			= cd_plano_w 
	and	coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w)	= ie_tipo_atendimento_w  LIMIT 1;
 
	if (qt_exige_desc_w > 0) then 
		ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(277659);
	end if;
end if;
 
ds_erro_p	:= ds_erro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
