CREATE OR REPLACE PROCEDURE obter_se_gera_lote_301 (ds_horarios_p text, ie_gerar_p INOUT text, dt_geracao_p INOUT timestamp) AS $body$
DECLARE


ds_horarios_w	c301_regra_lote.DS_HORA_EXECUCAO%type;
horarios_w	dbms_sql.varchar2_table;
ie_retorno_w	varchar(1);
dt_geracao_w	timestamp;
i		integer := 0;


BEGIN

ie_retorno_w	:= 'N';
ds_horarios_w	:= ds_horarios_p;

if (substr(ds_horarios_w,length(ds_horarios_w)-1,1) <> ';') then
	ds_horarios_w := ds_horarios_w||';';
end if;

horarios_w	:= obter_lista_string(ds_horarios_w,';');

for 	i in horarios_w.first..horarios_w.last loop
	if (substr(horarios_w(i),1,2) = to_char(clock_timestamp(),'hh24')) then
		ie_retorno_w 	:= 'S';
		dt_geracao_w	:= pkg_date_utils.get_time(clock_timestamp(),substr(horarios_w(i),1,2),59,59);
	end if;
end loop;

ie_gerar_p	:= ie_retorno_w;
dt_geracao_p	:= dt_geracao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
