SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_se_gerou_repasse ( nr_seq_protocolo_p bigint, ie_erro_p INOUT text) AS $body$
DECLARE

			 
ie_erro_w		varchar(1) := 'N';
qt_reg_w		bigint;


BEGIN 
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (nr_seq_protocolo_p <> 0) then 
 
	select	sum(coalesce(qt_reg,0)) 
	into STRICT	qt_reg_w	 
	from ( 
		SELECT	count(*) qt_reg 
		FROM conta_paciente c, procedimento_paciente b
LEFT OUTER JOIN procedimento_repasse a ON (b.nr_sequencia = a.nr_seq_procedimento)
WHERE b.nr_interno_conta	= c.nr_interno_conta and c.nr_seq_protocolo	= nr_seq_protocolo_p and coalesce(a.vl_repasse,0) 	= 0 and b.ie_responsavel_credito= 'RM' 
		 
union all
 
		SELECT	count(*) qt_reg 
		FROM conta_paciente c, material_atend_paciente b
LEFT OUTER JOIN material_repasse a ON (b.nr_sequencia = a.nr_seq_material)
WHERE b.nr_interno_conta	= c.nr_interno_conta and c.nr_seq_protocolo	= nr_seq_protocolo_p and coalesce(a.vl_repasse,0) 	= 0 and b.ie_responsavel_credito= 'RM' 
		 ) alias8;
	 
end if;
 
if (qt_reg_w > 0) then 
	ie_erro_w	:= 'S';
end if;
 
ie_erro_p	:= ie_erro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
