CREATE OR REPLACE PROCEDURE obter_se_linha_fluxo_agrup ( nr_linha_p bigint, nr_linha_sup_p bigint, ie_consiste_p INOUT text) AS $body$
DECLARE


ie_consiste_w	varchar(1)	:= 'S';
nr_linha_w	fluxo_caixa_agrup_linha.nr_sequencia%type;
nr_linha_sup_w	fluxo_caixa_agrup_linha.nr_sequencia%type;
nr_sequencia_w	w_fluxo_caixa_agrup_linha.nr_sequencia%type;


BEGIN

delete FROM w_fluxo_caixa_agrup_linha;

nr_linha_sup_w	:= nr_linha_sup_p;

select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_sequencia_w
from	w_fluxo_caixa_agrup_linha;

insert into w_fluxo_caixa_agrup_linha(
	nr_sequencia,
	nr_seq_linha,
	nr_seq_superior)
values (nr_sequencia_w,
	nr_linha_p,
	nr_linha_sup_w);
commit;

if (nr_linha_p IS NOT NULL AND nr_linha_p::text <> '') and (nr_linha_sup_p IS NOT NULL AND nr_linha_sup_p::text <> '') then

	loop
	begin
	nr_linha_w	:= nr_linha_sup_w;

	select	a.nr_seq_superior
	into STRICT	nr_linha_sup_w
	from	fluxo_caixa_agrup_linha a
	where	a.nr_sequencia = nr_linha_sup_w;

	select	max(nr_sequencia) + 1
	into STRICT	nr_sequencia_w
	from	w_fluxo_caixa_agrup_linha;

	insert into w_fluxo_caixa_agrup_linha(
		nr_sequencia,
		nr_seq_linha,
		nr_seq_superior)
	values (nr_sequencia_w,
		nr_linha_w,
		nr_linha_sup_w);
	commit;

	select	coalesce(max('S'),'N')
	into STRICT	ie_consiste_w
	from	w_fluxo_caixa_agrup_linha
	where	nr_seq_linha = nr_linha_sup_w;

	end;
	exit when(coalesce(nr_linha_sup_w::text, '') = '') or (ie_consiste_w = 'S');
	end loop;
end if;

ie_consiste_p	:= ie_consiste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
