CREATE OR REPLACE PROCEDURE obter_se_medico_disponivel (nr_seq_agenda_p bigint, cd_medico_p text) AS $body$
DECLARE



ie_existe_w		varchar(1);
ie_indisponivel_w		varchar(1);
dt_agendamento_w		timestamp;
dt_inicio_indisp_w		timestamp;
dt_fim_indisp_w		timestamp;


C01 CURSOR FOR
	SELECT	dt_inicio,
		dt_fim
	from	indisp_medico_cirurgia
	where	cd_medico  = cd_medico_p;


BEGIN

select	coalesce(max('S'),'N')
into STRICT 	ie_existe_w
from	indisp_medico_cirurgia
where	cd_medico  = cd_medico_p;

if (ie_existe_w = 'S') then

	select	to_date(to_char(trunc(dt_agenda),'dd/mm/yyyy') ||' '||to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	into STRICT 	dt_agendamento_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	open c01;
		loop
		fetch c01 into
			dt_inicio_indisp_w,
			dt_fim_indisp_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

			select	coalesce(max('S'),'N')
			into STRICT	ie_indisponivel_w
			
			where	dt_agendamento_w between dt_inicio_indisp_w and dt_fim_indisp_w;

			if (ie_indisponivel_w = 'S') then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(282267);
			end if;

			end;
		end loop;
	close c01;

end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
