CREATE OR REPLACE PROCEDURE obter_se_medico_disp_encaixe (dt_agenda_p timestamp, cd_medico_p text, ds_retorno_p INOUT text) AS $body$
DECLARE



ie_existe_w			varchar(1);
ie_disponivel_w		varchar(1);
dt_inicio_indisp_w	timestamp;
dt_fim_indisp_w		timestamp;


C01 CURSOR FOR
	SELECT	dt_inicio,
			dt_fim
	from	indisp_medico_cirurgia
	where	cd_medico  = cd_medico_p
	and		coalesce(ie_situacao, 'A') = 'A';


BEGIN
ds_retorno_p := 'S';

select	coalesce(max('S'),'N')
into STRICT 	ie_existe_w
from	indisp_medico_cirurgia
where	cd_medico  = cd_medico_p;

if (ie_existe_w = 'S') then

	open c01;
		loop
		fetch c01 into
			dt_inicio_indisp_w,
			dt_fim_indisp_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

			select	coalesce(max('N'),'S')
			into STRICT	ie_disponivel_w
			
			where	dt_agenda_p between dt_inicio_indisp_w and dt_fim_indisp_w;

			if (ie_disponivel_w = 'N') then
				ds_retorno_p := 'N';
			end if;

			end;
		end loop;
	close c01;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
